import React, { Component } from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

const Wrapper = styled.div`

  background-image: url(img/thebandweb.jpg);
  background-position: center;
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  background-size: cover;
  justify-content: center;
  align-items: center;
  background-repeat: no-repeat;
  color: #305077;

  @media (max-width: 400px) {
    display: inline;
    min-height: 100vh;
    height: auto;
  }

  .socialMedia {
    height: 200pxl
  }

  .instagram {
    border: 2px solid #305077;
    padding: 10px 14px;
    border-radius: 4px;
    margin: 0 10px;
  }

  .instagram:hover {
    border: 2px solid #305077;
    background-color: #305077;
    padding: 10px 14px;
    border-radius: 4px;
  }

  a {
    text-decoration: none;
    color: #305077;
    transition: .2s ease;
  }

  a:hover {
    text-decoration: none;
    color: #a1dbaa;
  }

  h2 {
    font-size: 20px;
    margin-bottom: 10px;
    font-weight: 700;
  }

  .contact {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    background-color: #f8e682;
    padding: 0 5px;
    width: 100vw;
    position: fixed;
    bottom: 0;

    @media (max-width: 400px) {
      position: relative;
    }
  }

  .chunk:hover {
    border-top: 2px solid #305077;
    border-bottom: 2px solid #305077;
  }

  .chunk {
    margin: 5px 10px;
    padding: 10px 0;
    width: 250px;
    border-top: 2px solid rgba(255,255,255,0);
    border-bottom: 2px solid rgba(255,255,255,0);
    transition: .2s ease;
  }

`

const Logo = styled.div`

  height: 200px;
  width: 600px;
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
  background-image: url(img/logo.svg);
  margin: 40px;

  @media (max-width: 800px) {
    height: 120px;
    width: 300px;
    margin: 0;    
  }

`

class App extends Component {
  render() {
    return (
      <Wrapper>
        <Logo>
        </Logo>
        <div className="socialMedia">
          <a className="instagram" href="https://www.instagram.com/parkletsband/">Instagram</a>
          <a className="instagram" href="https://www.facebook.com/Parklets-303267193704426/">Facebook</a>        
        </div>
        <div className="contact">
          <div className="chunk">
            <a href="mailto:contact@parkletsband.com">contact@parkletsband.com</a>
          </div>
          <div className="chunk">
            <a href="mailto:booking@parkletsband.com">booking@parkletsband.com</a>
          </div>
          <div className="chunk">
            <a href="mailto:press@parkletsband.com">press@parkletsband.com</a>
          </div>
          <div className="chunk">
            <a href="mailto:management@parkletsband.com">management@parkletsband.com</a>
          </div>
          <div className="chunk">
            <a href="mailto:management@parkletsband.com">fans@parkletsband.com</a>
          </div>
        </div>

      </Wrapper>
    );
  }
}

export default App;
