import React, { Component } from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import anime from 'animejs';

const Wrapper = styled.div`

background: #424242;
  display: flex;
  min-height: 100vh;
  width: 100vw;
  justify-content: center;

  @media (max-width: 800px) {
    padding-top: 20px;
  }

  a {
    color: white;
    text-decoration: none;
  }

  a:hover {
    color: lightblue;
  }

`

const Newspaper= styled.div`

  border: 1px solid white;
  margin: 20px;
  width: 100%;
  max-width: 800px;
  min-height: 400px;
  color: white;
  padding: 40px;
  h1 {
    font-size: 30px;
  }
  hr {
    margin: 10px 0 20px;
  }
  img {
    max-width: 100%;
    margin: 5px 0;
  }
  h4 {
    text-align: center;
    margin-top: 10px;
    margin-bottom: 5px;
  }

  a {
    text-align: center;
    font-style: italic;
  }

`

class Home extends Component {
  render() {
    return (
      <Wrapper>
        <Newspaper>
          <h1>Parklets Presskit</h1>
          <hr />
          <h4>Band Photo</h4>
          <img src="img/thebandweb.jpg"></img>
          <hr />
          <h4>PNG</h4>
          <img src="img/parkletslogobox-01.png"></img>
          <img src="img/parkletslogobox-02.png"></img>
          <img src="img/parkletslogobox-03.png"></img>
          <hr />
          <h4>SVG</h4>
          <img src="img/parkletslogobox-01.svg"></img>
          <img src="img/parkletslogobox-02.svg"></img>
          <img src="img/parkletslogobox-03.svg"></img>
          <hr />
          <h4>EPS</h4>
          <a href="mailto:press@parkletsband.com">Please email for .eps formats</a>
          <hr />
          <h4>Get Down Single</h4>
          <img src="img/getdownsingle.jpg"></img>
          <img src="img/getdownsingle.png"></img>
          <h4>Official Colors</h4>
        </Newspaper>
      </Wrapper>
    );
  }
}

export default Home;
